import React, { useState } from "react";
import axios from "axios";

const ProdutoForm = ({ token, produtos, setProdutos, idEditando, setView, clearForm }) => {
    const [nome, setNome] = useState("");
    const [quantidade, setQuantidade] = useState("");
    const [preco, setPreco] = useState("");
    const [descricao, setDescricao] = useState("");
    const [imagem, setImagem] = useState(null); // Modificado para armazenar o arquivo de imagem
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData(); // Usando FormData para incluir o arquivo
        formData.append("id", idEditando || "");
        formData.append("nome", nome);
        formData.append("quantidade", quantidade);
        formData.append("preco", preco);
        formData.append("descricao", descricao);
        if (imagem) {
            formData.append("imagem", imagem);
        }

        try {
            if (idEditando) {
                // Editar produto
                await axios.put(`https://backend-aula.vercel.app/app/produtos`, formData, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "multipart/form-data",
                    },
                });
                setProdutos(produtos.map((p) => (p._id === idEditando ? { ...p, nome, quantidade, preco, descricao } : p)));
            } else {
                // Adicionar novo produto
                const response = await axios.post("https://backend-aula.vercel.app/app/produtos", formData, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "multipart/form-data",
                    },
                });
                setProdutos([...produtos, response.data]);
            }
            clearForm();
            setView("");
        } catch (err) {
            console.error("Erro ao salvar produto:", err);
            setError("Erro ao salvar produto.");
        }
    };

    return (
        <form onSubmit={handleSubmit} encType="multipart/form-data">
            <div>
                <label>Nome:</label>
                <input type="text" value={nome} onChange={(e) => setNome(e.target.value)} required />
            </div>
            <div>
                <label>Quantidade:</label>
                <input type="number" value={quantidade} onChange={(e) => setQuantidade(e.target.value)} required />
            </div>
            <div>
                <label>PreÃ§o:</label>
                <input type="number" value={preco} onChange={(e) => setPreco(e.target.value)} required />
            </div>
            <div>
                <label>DescriÃ§Ã£o:</label>
                <textarea value={descricao} onChange={(e) => setDescricao(e.target.value)} required></textarea>
            </div>
            <div>
                <label>Imagem:</label>
                <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setImagem(e.target.files[0])} // ObtÃ©m o arquivo da imagem
                />
            </div>
            {imagem && (
                <div>
                    <p>PrÃ©-visualizaÃ§Ã£o:</p>
                    <img src={URL.createObjectURL(imagem)} alt="PrÃ©-visualizaÃ§Ã£o" style={{ maxWidth: "200px" }} />
                </div>
            )}
            {error && <p style={{ color: "red" }}>{error}</p>}
            <button type="submit">Salvar Produto</button>
        </form>
    );
};

export default ProdutoForm;
