import React from "react";
import axios from "axios";

const ProdutoForm = ({
    idEditando,
    nome,
    quantidade,
    preco,
    descricao,
    imagem,
    setIdEditando,
    setNome,
    setQuantidade,
    setPreco,
    setDescricao,
    setImagem,
    setProdutos,
    produtos,
    setView,
    token,
    clearForm,
    setError,
}) => {
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Preparar dados para envio com FormData
        const formData = new FormData();
        formData.append("id", idEditando || "");
        formData.append("nome", nome);
        formData.append("quantidade", quantidade);
        formData.append("preco", preco);
        formData.append("descricao", descricao);
        if (imagem) {
            formData.append("imagem", imagem); // Adicionar imagem ao FormData
        }

        try {
            if (idEditando) {
                // Editar produto
                await axios.put(`https://backend-aula.vercel.app/app/produtos`, formData, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "multipart/form-data",
                    },
                });
                setProdutos(produtos.map((p) => (p._id === idEditando ? { ...p, nome, quantidade, preco, descricao } : p)));
            } else {
                // Criar novo produto
                const response = await axios.post("https://backend-aula.vercel.app/app/produtos", formData, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "multipart/form-data",
                    },
                });
                setProdutos([...produtos, response.data]);
            }
            clearForm();
            setView("");
        } catch (err) {
            console.error("Erro ao salvar produto:", err);
            setError("Erro ao salvar produto.");
        }
    };

    return (
        <form className="form-produtos" onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Nome"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Quantidade"
                value={quantidade}
                onChange={(e) => setQuantidade(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Preço"
                value={preco}
                onChange={(e) => setPreco(e.target.value)}
                required
            />
            <textarea
                placeholder="Descrição"
                value={descricao}
                onChange={(e) => setDescricao(e.target.value)}
                required
            />
            <input
                type="file"
                accept="image/*"
                onChange={(e) => setImagem(e.target.files[0])} // Atualizar estado com o arquivo
            />
            {imagem && (
                <div>
                    <p>Pré-visualização da imagem:</p>
                    <img src={URL.createObjectURL(imagem)} alt="Pré-visualização" className="preview-img" />
                </div>
            )}
            <button type="submit">{idEditando ? "Atualizar" : "Adicionar"} Produto</button>
        </form>
    );
};

export default ProdutoForm;
